const API_URL="https://www.ambassadoribet.com/_internal/gql",BRAND_ID="ab";async function fetchGraphQL(t,o={}){const e=extractAuthDataFromCookie();if(!e||!e.accessToken)throw new Error("Unable to retrieve authorization data.");const n=await fetch(API_URL,{method:"POST",headers:{"Content-Type":"application/json",Authorization:`Bearer ${e.accessToken}`},body:JSON.stringify({query:t,variables:o})});return await n.json()}function extractAuthDataFromCookie(){const t=getCookie("auth");if(!t)return console.error("Auth cookie not found."),null;try{const o=decodeURIComponent(t),e=JSON.parse(o);return{userId:e.userId,accessToken:e.accessToken}}catch(t){return console.error("Error parsing auth cookie:",t),null}}function getCookie(t){const o=document.cookie.split(";").find((o=>o.trim().startsWith(t+"=")));return o?decodeURIComponent(o.split("=")[1]):null}function formatDate(t){return new Date(t).toLocaleString()}